package week1.day1;

public class IsPrime {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 3;

		for (int i = 2; i <= 2; i++) {

			if (n % 2 == 0) {
				System.out.println("Its a Even no...");
			} else {
				System.out.println("Its a PRIME no...");
			}
		}

	}
}
